// The $ is the shorthand for a jquery function, you can then use jquery
// selectors which are essentially the same as css selectors, so here
// we select your select field and then bind a function to
// it's change event handler  "[name='nameofobject']" $('td[name="tcol1"]')
$('select[name="searchYear"]').change(function(){
    // You can access the value of your select field using the .val() method
    alert('Select field value has changed to' + $('select[name="searchYear"]').val());
    $('select[name="searchWeek"]').empty();
   // You can perform an ajax request using the .ajax() method
   $.ajax({
       type: 'POST',
       url: '/get_year_month', // This is the url that will be requested

      // This is an object of values that will be passed as GET variables and
      // available inside changeStatus.php as $_GET['selectFieldValue'] etc...
      data: JSON.stringify({year: $('select[name="searchYear"]').val(),
                            month: $('select[name="searchMonth"]').val() }),
      contentType: "application/json",
      dataType: "json",
      // This is what to do once a successful request has been completed - if
      // you want to do nothing then simply don't include it. But I suggest you
      // add something so that your use knows the db has been updated
      success: function(data){
            data = JSON.parse(data);
            $.each(data, function (k,v) {
                    var txt = '<option value = "'+k+'">'+v+'</option>'
                    $('select[name="searchWeek"]').append(txt);
                });
       },
       failure: function () {
            alert("Failed!");
        },
      dataType: 'html'
    });

});

$('select[name="searchMonth"]').change(function(){
    $('select[name="searchWeek"]').empty();
   $.ajax({
       type: 'POST',
       url: '/get_year_month',
      data: JSON.stringify({year: $('select[name="searchYear"]').val(),
                            month: $('select[name="searchMonth"]').val() }),
      contentType: "application/json",
      dataType: "json",
      success: function(data){
            data = JSON.parse(data);
            $.each(data, function (k,v) {
                    var txt = '<option value = "'+k+'">'+v+'</option>'
                    $('select[name="searchWeek"]').append(txt);
                });
       },
       failure: function () {
            alert("Failed!");
        },
      dataType: 'html'
    });
});

